(function(window) {

  function MenuEvents(config) {
    var self = this,
        menuBtn = document.getElementById(config.menuBtnId),
        menu = document.getElementById(config.menuId),
        navBtns = document.querySelectorAll(config.menuItems);

    menuBtn.addEventListener('click', toggleClass);

    for (var i = 0; i < navBtns.length; i += 1) {
      navBtns[i].addEventListener('click', scrollToSection);
    }

    function toggleClass() {
      if(menu.className === config.menuClassVisible) {
        menu.className = config.menuClassHidden;
      } else if(menu.className === config.menuClassHidden) {
        menu.className = config.menuClassVisible;
      }
    }

    function scrollToSection(event) {
      (event.preventDefault) ? 
          event.preventDefault() : event.returnValue = false;

      var target = (event.currentTarget) ?
                      event.currentTarget : event.srcElement;

      if (target.id === config.menuBtnId) {
        return;
      }

      var element = document.getElementById(
          target.href.substring(target.href.indexOf('#') + 1));

      TweenLite.to(window, 0.5, { 
        scrollTo: { 
          y: element.offsetTop
        }
      });
      
      toggleClass();
    }

  }

  var toggleMenu = new MenuEvents({
    menuBtnId: 'menu-btn',
    menuId: 'navigation-menu',
    menuClassVisible: 'navigation visible',
    menuClassHidden: 'navigation hidden',
    menuItems: '.navigation-item'
  });

  echo.init({
    offset: 100,
    throttle: 250,
    unload: false
  });

}(window));
